static void *
xmalloc(size_t len)
{
	void *buf;

	if(len == 0) {
		return NULL;
	}
	buf = malloc(len);
	if(buf == NULL) {
		fprintf(stderr, "memory allocation error\n");
		exit(EXIT_FAILURE);
	}
	return buf;
}

static void
xfree(void *buf)
{
	if(buf != NULL) {
		free(buf);
	}
}

static size_t
hextobin(unsigned char *buf, size_t max_len, const char *src)
{
	size_t u;
	int acc, z;

	u = 0;
	acc = 0;
	z = 0;
	for(;;) {
		int c;

		c = *src ++;
		if(c == 0) {
			if(z) {
				fprintf(stderr, "Lone hex nibble\n");
				exit(EXIT_FAILURE);
			}
			return u;
		}
		if(c >= '0' && c <= '9') {
			c -= '0';
		} else if(c >= 'A' && c <= 'F') {
			c -= 'A' - 10;
		} else if(c >= 'a' && c <= 'f') {
			c -= 'a' - 10;
		} else if(c == ' ' || c == '\t' || c == '\r' || c == '\n') {
			continue;
		} else {
			fprintf(stderr, "Not an hex digit: U+%04X\n",
				(unsigned)c);
			exit(EXIT_FAILURE);
		}
		if(z) {
			if(u >= max_len) {
				fprintf(stderr,
					"Hex string too long for buffer\n");
				exit(EXIT_FAILURE);
			}
			buf[u ++] = (unsigned char)((acc << 4) + c);
		} else {
			acc = c;
		}
		z = !z;
	}
}

static void
check_eq(const void *a, const void *b, size_t len, const char *banner)
{
	size_t u;

	if(memcmp(a, b, len) == 0) {
		return;
	}
	fprintf(stderr, "%s: wrong value:\n", banner);
	fprintf(stderr, "a: ");
	for(u = 0; u < len; u ++) {
		fprintf(stderr, "%02x", ((const unsigned char *)a)[u]);
	}
	fprintf(stderr, "\n");
	fprintf(stderr, "b: ");
	for(u = 0; u < len; u ++) {
		fprintf(stderr, "%02x", ((const unsigned char *)b)[u]);
	}
	fprintf(stderr, "\n");
	exit(EXIT_FAILURE);
}

static void
test_SHAKE_KAT(int capacity,
	const unsigned char *src, size_t ilen, const char *hexout)
{
	unsigned char ref[500], tmp[500];
	size_t olen, u;
	shake_context sc;

	olen = hextobin(ref, sizeof ref, hexout);
	shake_init(&sc, capacity);
	shake_inject(&sc, src, ilen);
	shake_flip(&sc);
	shake_extract(&sc, tmp, olen);
	check_eq(ref, tmp, olen, "SHAKE KAT 1");

	memset(tmp, 0, sizeof tmp);
	shake_init(&sc, capacity);
	for(u = 0; u < ilen; u ++) {
		shake_inject(&sc, src + u, 1);
	}
	shake_flip(&sc);
	for(u = 0; u < olen; u ++) {
		shake_extract(&sc, tmp + u, 1);
	}
	check_eq(ref, tmp, olen, "SHAKE KAT 2");
}

static void
test_SHAKE_KATstr(int capacity, const char *src, const char *hexout)
{
	test_SHAKE_KAT(capacity, (const unsigned char *)src,
		strlen(src), hexout);
}

static void
test_SHAKE_KAThex(int capacity, const char *hexsrc, const char *hexout)
{
	unsigned char src[500];
	size_t src_len;

	src_len = hextobin(src, sizeof src, hexsrc);
	test_SHAKE_KAT(capacity, src, src_len, hexout);
}

static void
test_SHAKE128(void)
{
	printf("Test SHAKE128: ");
	fflush(stdout);
	test_SHAKE_KATstr(256, "",
    "7f9c2ba4e88f827d616045507605853ed73b8093f6efbc88eb1a6eacfa66ef26");
	test_SHAKE_KATstr(256, "The quick brown fox jumps over the lazy dog",
    "f4202e3c5852f9182a0430fd8144f0a74b95e7417ecae17db0f8cfeed0e3e66e");
	test_SHAKE_KATstr(256, "The quick brown fox jumps over the lazy dof",
    "853f4538be0db9621a6cea659a06c1107b1f83f02b13d18297bd39d7411cf10c");
	printf("done.\n");
	fflush(stdout);
}

static void
test_SHAKE256(void)
{
	printf("Test SHAKE256: ");
	fflush(stdout);
	test_SHAKE_KATstr(512, "",
    "46b9dd2b0ba88d13233b3feb743eeb243fcd52ea62b81b82b50c27646ed5762f"
    "d75dc4ddd8c0f200cb05019d67b592f6fc821c49479ab48640292eacb3b7c4be");
	test_SHAKE_KAThex(512, "", "2bac5716803a9cda8f9e84365ab0a681327b5ba34fdedfb1c12e6e807f45284b");
	test_SHAKE_KAThex(512, "8d8001e2c096f1b88e7c9224a086efd4797fbf74a8033a2d422a2b6b8f6747e4", "");
	printf("done.\n");
	fflush(stdout);
}

static void
test_RNG(void)
{
	shake_context sc;
	prng p;
	unsigned char hv[100][32];
	size_t u;
	int t;

	/*
	 * We test ChaCha20 by filling the block a hundred times, and
	 * then checking that we obtained a hundred distinct values.
	 */
	printf("Test PRNG/ChaCha20: ");
	fflush(stdout);

	shake_init(&sc, 512);
	shake_flip(&sc);
	if(!falcon_prng_init(&p, &sc, PRNG_CHACHA20)) {
		fprintf(stderr, "failed PRNG/ChaCha20 initialization\n");
		exit(EXIT_FAILURE);
	}

	for(u = 0; u < 100; u ++) {
		size_t v;

		shake_init(&sc, 512);
		shake_inject(&sc, p.buf.d, sizeof p.buf.d);
		shake_flip(&sc);
		shake_extract(&sc, hv[u], 32);
		for(v = 0; v < u; v ++) {
			if(memcmp(hv[v], hv[u], 32) == 0) {
				fprintf(stderr, "ChaCha20 stutter\n");
				exit(EXIT_FAILURE);
			}
		}
		falcon_prng_refill(&p);
		printf(".");
		fflush(stdout);
	}

	printf(" done.\n");

	/*
	 * ChaCha20/SSE2, if available, should produce the exact same
	 * values as ChaCha20.
	 */
	printf("Test PRNG/ChaCha20-SSE2: ");
	fflush(stdout);

	shake_init(&sc, 512);
	shake_flip(&sc);
	if(falcon_prng_init(&p, &sc, PRNG_CHACHA20_SSE2)) {
		for(u = 0; u < 100; u ++) {
			unsigned char tmp[32];

			shake_init(&sc, 512);
			shake_inject(&sc, p.buf.d, sizeof p.buf.d);
			shake_flip(&sc);
			shake_extract(&sc, tmp, 32);
			if(memcmp(hv[u], tmp, 32) != 0) {
				fprintf(stderr, "ChaCha20-SSE2 mismatch\n");
				exit(EXIT_FAILURE);
			}
			falcon_prng_refill(&p);
			printf(".");
			fflush(stdout);
		}

		printf(" done.\n");
	} else {
		printf("UNAVAILABLE\n");
	}

	/*
	 * For AES-NI, we do not have a reference implementation, so we
	 * only check for no stutter.
	 */
	printf("Test PRNG/AES-x86ni: ");
	fflush(stdout);

	shake_init(&sc, 512);
	shake_flip(&sc);
	if(falcon_prng_init(&p, &sc, PRNG_AES_X86NI)) {
		for(u = 0; u < 100; u ++) {
			size_t v;

			shake_init(&sc, 512);
			shake_inject(&sc, p.buf.d, sizeof p.buf.d);
			shake_flip(&sc);
			shake_extract(&sc, hv[u], 32);
			for(v = 0; v < u; v ++) {
				if(memcmp(hv[v], hv[u], 32) == 0) {
					fprintf(stderr, "AES-NI stutter\n");
					exit(EXIT_FAILURE);
				}
			}
			falcon_prng_refill(&p);
			printf(".");
			fflush(stdout);
		}
		printf(" done.\n");
	} else {
		printf("UNAVAILABLE\n");
	}

	shake_init(&sc, 512);
	shake_flip(&sc);
	printf("Default PRNG: ");
	t = falcon_prng_init(&p, &sc, 0);
	switch (t) {
	case PRNG_CHACHA20:
		printf("ChaCha20 (portable)\n");
		break;
	case PRNG_CHACHA20_SSE2:
		printf("ChaCha20 (SSE2)\n");
		break;
	case PRNG_AES_X86NI:
		printf("AES (x86ni)\n");
		break;
	default:
		printf("UNKNOWN (%d)\n", t);
		break;
	}
	fflush(stdout);
}

