static unsigned char *
encode_skey(int comp, unsigned q, const int16_t *f, const int16_t *g,
	const int16_t *F, const int16_t *G, unsigned logn, size_t *skey_len)
{
	int i;
	unsigned char *out;
	size_t out_len;

	out = NULL;
	out_len = (size_t)-1;
	for(i = 0;; i ++) {
		size_t off, len;

		off = 1;
		len = falcon_encode_small((out ? out + off : NULL),
			out_len - off, comp, q, f, logn);
		if(len == 0) {
			fprintf(stderr, "bad key element\n");
			exit(EXIT_FAILURE);
		}
		off += len;
		len = falcon_encode_small((out ? out + off : NULL),
			out_len - off, comp, q, g, logn);
		if(len == 0) {
			fprintf(stderr, "bad key element\n");
			exit(EXIT_FAILURE);
		}
		off += len;
		len = falcon_encode_small((out ? out + off : NULL),
			out_len - off, comp, q, F, logn);
		if(len == 0) {
			fprintf(stderr, "bad key element\n");
			exit(EXIT_FAILURE);
		}
		off += len;
		len = falcon_encode_small((out ? out + off : NULL),
			out_len - off, comp, q, G, logn);
		if(len == 0) {
			fprintf(stderr, "bad key element\n");
			exit(EXIT_FAILURE);
		}
		off += len;

		if(i == 0) {
			out_len = off;
			out = xmalloc(out_len);
		} else {
			if(off != out_len) {
				fprintf(stderr, "length error\n");
				exit(EXIT_FAILURE);
			}
			out[0] = (comp << 5) | logn;
			*skey_len = out_len;
			return out;
		}
	}
}

static unsigned
decode_skey(const void *src, size_t len,
	unsigned *q, int16_t **f, int16_t **g, int16_t **F, int16_t **G)
{
	const unsigned char *buf;
	int fb, comp;
	unsigned logn;
	int16_t ske[4][1024];
	int i;
	size_t zlen;

	buf = src;
	if(len == 0) {
		fprintf(stderr, "invalid key (empty)\n");
		exit(EXIT_FAILURE);
	}
	fb = *buf ++;
	len --;
	logn = fb & 0x0F;
	if(logn == 0 || logn > 10) {
		fprintf(stderr, "invalid key (logn)\n");
		exit(EXIT_FAILURE);
	}
	*q = 12289;
	comp = fb >> 5;
	for(i = 0; i < 4; i ++) {
		size_t elen;

		elen = falcon_decode_small(ske[i], logn, comp, *q, buf, len);
		if(elen == 0) {
			fprintf(stderr, "invalid key element\n");
			exit(EXIT_FAILURE);
		}
		buf += elen;
		len -= elen;
	}
	if(len != 0) {
		fprintf(stderr, "trailing garbage after key\n");
		exit(EXIT_FAILURE);
	}
	zlen = sizeof(int16_t) << logn;
	*f = xmalloc(zlen);
	*g = xmalloc(zlen);
	*F = xmalloc(zlen);
	*G = xmalloc(zlen);
	memcpy(*f, ske[0], zlen);
	memcpy(*g, ske[1], zlen);
	memcpy(*F, ske[2], zlen);
	memcpy(*G, ske[3], zlen);
	return logn;
}

static void
test_key_codec_comp_key(int comp, unsigned q,
	const int16_t *f_src, const int16_t *g_src,
	const int16_t *F_src, const int16_t *G_src, unsigned logn)
{
	int16_t *f, *g, *F, *G;
	void *skey;
	size_t len, n;
	unsigned q2, logn2;

	skey = encode_skey(comp, q, f_src, g_src, F_src, G_src, logn, &len);
	printf("<q=%u,logn=%u->len=%lu>", q, logn, (unsigned long)len);
	fflush(stdout);
	logn2 = decode_skey(skey, len, &q2, &f, &g, &F, &G);
	if(logn != logn2) {
		fprintf(stderr, "decode mismatch (logn)\n");
		exit(EXIT_FAILURE);
	}
	if(q != q2) {
		fprintf(stderr, "decode mismatch (q)\n");
		exit(EXIT_FAILURE);
	}
	n = sizeof(int16_t) << logn;
	check_eq(f, f_src, n, "f");
	check_eq(g, g_src, n, "g");
	check_eq(F, F_src, n, "F");
	check_eq(G, G_src, n, "G");
	xfree(f);
	xfree(g);
	xfree(F);
	xfree(G);
	xfree(skey);
}

