/*
 * Make a polynomial with random coefficients in -512..511 (uniform).
 */
static void
mk_rand_poly3(prng *p, fpr *f, unsigned logn, unsigned full)
{
	size_t u, n;

	n = (size_t)(1 + (full << 1)) << (logn - full);
	for(u = 0; u < n; u ++) {
		int32_t x;
		
		x = falcon_prng_get_u8(p);
		x = (x << 8) + falcon_prng_get_u8(p);
		x &= 0x3FF;
		f[u] = fpr_of(x - 512);
	}
}

static void
test_poly3_inner(unsigned logn, unsigned full)
{
	unsigned long ctr, num;
	shake_context rng;
	prng p;
	unsigned char xb;
	size_t n;

	printf("[%u]", logn);
	fflush(stdout);

	n = (size_t)(1 + (full << 1)) << (logn - full);
	shake_init(&rng, 512);
	xb = logn;
	shake_inject(&rng, &xb, 1);
	shake_flip(&rng);
	falcon_prng_init(&p, &rng, PRNG_CHACHA20);
	num = 262144UL >> logn;
	for(ctr = 0; ctr < num; ctr ++) {
		fpr f[768], g[768], h[768];
		fpr f0[384], f1[384], f2[384], g0[384], g1[384], g2[384];
		size_t u;

		mk_rand_poly3(&p, f, logn, full);
		memcpy(g, f, n * sizeof *f);
		falcon_FFT3(g, logn, full);
		falcon_iFFT3(g, logn, full);
		for(u = 0; u < n; u ++) {
			if(fpr_rint(f[u]) != fpr_rint(g[u])) {
				fprintf(stderr, "FFT3/iFFT3 error\n");
				exit(EXIT_FAILURE);
			}
		}
		mk_rand_poly3(&p, g, logn, full);
		for(u = 0; u < n; u ++) {
			h[u] = fpr_of(0);
		}
		for(u = 0; u < n; u ++) {
			size_t v;

			for(v = 0; v < n; v ++) {
				fpr s;
				size_t k;

				s = fpr_mul(f[u], g[v]);
				k = u + v;
				if(k >= n + (n >> 1)) {
					size_t k1;

					k1 = k - n - (n >> 1);
					h[k1] = fpr_sub(h[k1], s);
				} else if(k >= n) {
					size_t k1, k2;

					k1 = k - n;
					k2 = k1 + (n >> 1);
					h[k1] = fpr_sub(h[k1], s);
					h[k2] = fpr_add(h[k2], s);
				} else {
					h[k] = fpr_add(h[k], s);
				}
			}
		}
		falcon_FFT3(f, logn, full);
		falcon_FFT3(g, logn, full);
		falcon_poly_mul_fft3(f, g, logn, full);
		falcon_iFFT3(f, logn, full);
		for(u = 0; u < n; u ++) {
			if(fpr_rint(f[u]) != fpr_rint(h[u])) {
				fprintf(stderr, "FFT3 mul error\n");
				exit(EXIT_FAILURE);
			}
		}

		if(logn >= 2 && !full) {
			mk_rand_poly3(&p, f, logn, 0);
			memcpy(h, f, n * sizeof *f);
			falcon_FFT3(f, logn, 0);
			falcon_poly_split_deep_fft3(f0, f1, f, logn);
			memcpy(g0, f0, (n >> 1) * sizeof *f0);
			memcpy(g1, f1, (n >> 1) * sizeof *f1);
			falcon_iFFT3(g0, logn - 1, 0);
			falcon_iFFT3(g1, logn - 1, 0);
			for(u = 0; u < (n >> 1); u ++) {
				if(fpr_rint(g0[u])
						!= fpr_rint(h[(u << 1) + 0])
					|| fpr_rint(g1[u])
						!= fpr_rint(h[(u << 1) + 1]))
				{
					fprintf(stderr, "split error\n");
					exit(EXIT_FAILURE);
				}
			}

			falcon_poly_merge_deep_fft3(g, f0, f1, logn);
			falcon_iFFT3(g, logn, 0);
			for(u = 0; u < n; u ++) {
				if(fpr_rint(g[u]) != fpr_rint(h[u])) {
					fprintf(stderr, "merge error\n");
					exit(EXIT_FAILURE);
				}
			}
		}

		if(logn >= 2 && full) {
			size_t tn;

			tn = (size_t)1 << (logn - 1);
			mk_rand_poly3(&p, f, logn, 1);
			memcpy(h, f, n * sizeof *f);
			falcon_FFT3(f, logn, 1);
			falcon_poly_split_top_fft3(f0, f1, f2, f, logn);
			memcpy(g0, f0, tn * sizeof *f0);
			memcpy(g1, f1, tn * sizeof *f1);
			memcpy(g2, f2, tn * sizeof *f2);
			falcon_iFFT3(g0, logn - 1, 0);
			falcon_iFFT3(g1, logn - 1, 0);
			falcon_iFFT3(g2, logn - 1, 0);
			for(u = 0; u < tn; u ++) {
				if(fpr_rint(g0[u])
						!= fpr_rint(h[3 * u + 0])
					|| fpr_rint(g1[u])
						!= fpr_rint(h[3 * u + 1])
					|| fpr_rint(g2[u])
						!= fpr_rint(h[3 * u + 2]))
				{
					fprintf(stderr, "split error\n");
					exit(EXIT_FAILURE);
				}
			}

			falcon_poly_merge_top_fft3(g, f0, f1, f2, logn);
			falcon_iFFT3(g, logn, 1);
			for(u = 0; u < n; u ++) {
				if(fpr_rint(g[u]) != fpr_rint(h[u])) {
					fprintf(stderr, "merge error\n");
					exit(EXIT_FAILURE);
				}
			}
		}

		if(((ctr + 1) & 0xFF) == 0) {
			printf(".");
			fflush(stdout);
		}
	}
}

