static void
speed_falcon(unsigned logn, unsigned ter)
{
	falcon_keygen *fk;
	falcon_sign *fs;
	falcon_vrfy *fv;
	unsigned char skey[6000], pkey[3000], sig[3000];
	unsigned char nonce[40];
	size_t skey_len, pkey_len;
	long num;
	uint64_t total_sig_len, num_sig;
	size_t sig_len, max_sig_len;

	printf("N=%u: ", (1 + (ter << 1)) << (logn - ter));
	if(logn < 10) {
		printf(" ");
	}
	fflush(stdout);

	fk = falcon_keygen_new(logn, ter);
	if(fk == NULL) {
		fprintf(stderr, "keygen context creation error\n");
		exit(EXIT_FAILURE);
	}
	skey_len = sizeof skey;
	pkey_len = sizeof pkey;
	if(!falcon_keygen_make(fk, FALCON_COMP_STATIC,
		skey, &skey_len, pkey, &pkey_len))
	{
		fprintf(stderr, "keygen error\n");
		exit(EXIT_FAILURE);
	}
	falcon_keygen_free(fk);

	fs = falcon_sign_new();
	if(fs == NULL) {
		fprintf(stderr, "sign context creation error\n");
		exit(EXIT_FAILURE);
	}
	if(!falcon_sign_set_private_key(fs, skey, skey_len)) {
		fprintf(stderr, "error loading private key\n");
		exit(EXIT_FAILURE);
	}
	total_sig_len = 0;
	num_sig = 0;
	max_sig_len = 0;
	num = 1;
	for(;;) {
		long j;
		clock_t begin, end;
		double tt;

		begin = clock();
		for(j = 0; j < num; j ++) {
			falcon_sign_start(fs, nonce);
			falcon_sign_update(fs, "test", 4);
			sig_len = falcon_sign_generate(fs, sig, sizeof sig,
				FALCON_COMP_STATIC);
			if(sig_len == 0) {
				fprintf(stderr, "signature failure\n");
				exit(EXIT_FAILURE);
			}
			total_sig_len += (uint64_t)sig_len;
			num_sig ++;
			if(sig_len > max_sig_len) {
				max_sig_len = sig_len;
			}
		}
		end = clock();
		tt = (double)(end - begin) / CLOCKS_PER_SEC;
		if(tt < 2.0) {
			num <<= 1;
			continue;
		}
		printf(" %11.3f sig/s  (len = %7.2f [max: %4u])",
			(double)num / tt,
			(double)total_sig_len / (double)num_sig,
			(unsigned)max_sig_len);
		fflush(stdout);
		break;
	}
	falcon_sign_free(fs);

	fv = falcon_vrfy_new();
	if(fv == NULL) {
		fprintf(stderr, "context creation error\n");
		exit(EXIT_FAILURE);
	}
	if(!falcon_vrfy_set_public_key(fv, pkey, pkey_len)) {
		fprintf(stderr, "error loading public key\n");
		exit(EXIT_FAILURE);
	}
	num = 1;
	for(;;) {
		long j;
		clock_t begin, end;
		double tt;
		int z;

		begin = clock();
		z = 1;
		for(j = 0; j < num; j ++) {
			falcon_vrfy_start(fv, nonce, sizeof nonce);
			falcon_vrfy_update(fv, "test", 4);
			z &= falcon_vrfy_verify(fv, sig, sig_len) > 0;
		}
		end = clock();
		if(!z) {
			fprintf(stderr, "verify failure\n");
			exit(EXIT_FAILURE);
		}
		tt = (double)(end - begin) / CLOCKS_PER_SEC;
		if(tt < 2.0) {
			num <<= 1;
			continue;
		}
		printf(" %12.3f vrf/s", (double)num / tt);
		fflush(stdout);
		break;
	}
	falcon_vrfy_free(fv);

	printf("\n");
	fflush(stdout);
}

